INSERT INTO `safety` (`id`, `title`, `intro`, `content`, `admin_add`, `site_title`, `slug`, `tags`, `status`, `meta_desc`, `meta_key`, `image_link`, `created`, `feature`, `count_view`) VALUES
(1, 'Thịt bò khô giả tràn lan trên thị trường', 'Các chiêu trò sử dụng nguyên liệu "giả" làm thịt bò khô có rất nhiều. Người tiêu dùng cần biết các nhận biết để đảm bảo sức khỏe cho chình mình', '<p><span style="font-size:14px"><span style="background-color:#ffffff; color:#000000; font-family:Lato-Regular,Arial,Helvetica,sans-serif">C&aacute;c chi&ecirc;u tr&ograve; sử dụng nguy&ecirc;n liệu &quot;giả&quot; l&agrave;m thịt b&ograve; kh&ocirc; c&oacute; rất nhiều. Người ti&ecirc;u d&ugrave;ng cần biết c&aacute;c nhận biết để đảm bảo sức khỏe cho ch&igrave;nh m&igrave;nh</span></span></p>\r\n\r\n<h3 style="color:#000000; font-style:normal; text-align:justify"><span style="font-size:14px"><span style="font-family:Lato-Regular ! important"><strong>C&aacute;ch ph&acirc;n biệt thịt b&ograve; kh&ocirc; bị l&agrave;m &quot;giả&quot;</strong></span></span></h3>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">C&agrave;ng gần Tết, thịt b&ograve; kh&ocirc; được nhiều người ti&ecirc;u d&ugrave;ng t&igrave;m mua. Gần đ&acirc;y, cơ quan chức năng li&ecirc;n tiếp ph&aacute;t hiện những cơ sở chế biến thịt b&ograve; kh&ocirc; được l&agrave;m từ phổi lợn c&ugrave;ng với h&oacute;a chất mất an to&agrave;n vệ sinh thực phẩm khiến nhiều người kinh h&atilde;i.&nbsp;</span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">Những vụ l&agrave;m giả thịt b&ograve; kh&ocirc; li&ecirc;n tiếp bị ph&aacute;t hiện gần đ&acirc;y cho thấy sự bất an của người ti&ecirc;u d&ugrave;ng về chất lượng của sản phẩm n&agrave;y.&nbsp;</span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:Lato-Regular ! important">PGS.TS Nguyễn Thị L&acirc;m &ndash; viện ph&oacute; Viện Dinh dưỡng Quốc gia cho hay, b&ograve; kh&ocirc; được l&agrave;m bằng thịt lợn hay thịt b&ograve; cũng đều l&agrave; nguồn cung cấp chất đạm, tuy nhi&ecirc;n ch&uacute;ng g&acirc;y kh&oacute; ti&ecirc;u hơn thịt tươi. Việc đ&aacute;nh tr&aacute;o giữa hai loại thịt n&agrave;y đ&atilde; đ&aacute;nh lừa người ti&ecirc;u d&ugrave;ng. Đặc biệt, nếu sử dụng nguồn thịt kh&ocirc;ng đảm bảo sẽ g&acirc;y hại cho người ăn. Hiện, danh mục c&aacute;c chất phụ gia cho ph&eacute;p sử dụng trong thực phẩm c&oacute; một số loại hương liệu tạo m&ugrave;i b&ograve;, song nếu người sử dụng kh&ocirc;ng kiểm so&aacute;t được mặt liều lượng sẽ g&acirc;y nguy hiểm cho người ăn.&nbsp;</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px">Về ph&acirc;n biệt, PGS L&acirc;m thừa nhận rất kh&oacute;, chủ yếu vẫn l&agrave; việc t&igrave;m những địa chỉ tin cậy, uy t&iacute;n.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'vũ thế anh ', '', 'thit-bo-kho-gia-tr-n-lan-tren-thi-truong', '[""]', 1, '                                                                                    ', '                                            \r\n                                    ', 'http://localhost/tuoisach/upload/images/anh%20ve%20sinh%20an%20toan%20thuc%20pham/thit-bo-kho-gia.jpg', 1491258315, '0', 9),
(2, 'thịt bẩn tràn lan', 'Ập vào kiểm tra cửa hàng thực phẩm ở TP HCM, lực lượng chức năng phát hiện nhiều thịt bẩn. Nhân viên khai thực phẩm này bán cho các quán ăn làm mồi nhậu, bếp ăn tập thể.', '<p><span style="font-size:14px"><span style="color:#222222">Ập v&agrave;o kiểm tra cửa h&agrave;ng thực phẩm ở TP HCM, lực lượng chức năng ph&aacute;t hiện nhiều thịt bẩn. Nh&acirc;n vi&ecirc;n khai thực phẩm n&agrave;y b&aacute;n cho c&aacute;c qu&aacute;n ăn l&agrave;m mồi nhậu, bếp ăn tập thể.</span></span></p>\r\n', 'vũ thế anh ', '', 'thit-ban-tr-n-lan', '[""]', 1, '                                                                                    ', '                                            \r\n                                    ', 'http://localhost/tuoisach/upload/images/anh%20ve%20sinh%20an%20toan%20thuc%20pham/ho-bien-thit-lon-thanh-thit-bo.jpg', 1491258300, '0', 10),
(3, 'Trang trại mổ lợn bẩn ', 'Không hôi thối, không bẩn, không nước thải, toàn bộ quy trình chăn nuôi, sơ chế, đóng gói được tổ chức khép kín, một trang trại lợn ở ngoại thành Hà Nội được ví như hệ thống 5 sao.', '<p>Kh&ocirc;ng h&ocirc;i thối, kh&ocirc;ng bẩn, kh&ocirc;ng nước thải, to&agrave;n bộ quy tr&igrave;nh chăn nu&ocirc;i, sơ chế, đ&oacute;ng g&oacute;i được tổ chức kh&eacute;p k&iacute;n, một trang trại lợn ở ngoại th&agrave;nh H&agrave; Nội được v&iacute; như hệ thống 5 sao.</p>\r\n', 'vũ thế anh ', '', 'trang-trai-mo-lon-ban', '[""]', 1, '                                                                                                                                                                        ', '                                                                                  \r\n                                          \r\n                                    ', 'http://localhost/tuoisach/upload/images/anh%20ve%20sinh%20an%20toan%20thuc%20pham/trang-trai-mo-lon-ban.jpg', 1491258283, '', 8),
(4, 'Cảnh tượng hô biến thịt lợn thối thành thịt bò với giá cao', 'Sử dụng hóa chất để tẩm ướp, biến thịt lợn thành thịt bò là hành động coi thường sức khỏe của người tiêu dùng.', '<p>Sử dụng h&oacute;a chất để tẩm ướp, biến thịt lợn th&agrave;nh thịt b&ograve; l&agrave; h&agrave;nh động coi thường sức khỏe của người ti&ecirc;u d&ugrave;ng.</p>\r\n', 'vũ thế anh ', '', 'canh-tuong-ho-bien-thit-lon-thoi-th-nh-thit-bo-voi-gia-cao', '[""]', 1, '                                                                                    ', '                                            \r\n                                    ', 'http://localhost/tuoisach/upload/images/anh%20ve%20sinh%20an%20toan%20thuc%20pham/thit-ban.jpg', 1491258265, '0', 14),
(5, 'Tiêu hủy lợn thối khia vào Hà Nội', 'Hội đồng tiêu hủy, Công an tỉnh Bình Phước đã hủy gần 6 tấn thịt, xương, nội tạng lợn, các sản phẩm động vật không rõ nguồn gốc và không đảm bảo tiêu chuẩn vệ sinh an toàn thực phẩm.', '<p>H&ocirc;̣i đ&ocirc;̀ng ti&ecirc;u hủy, C&ocirc;ng an tỉnh Bình Phước đ&atilde; hủy g&acirc;̀n 6 tấn thịt, xương, nội tạng lợn, c&aacute;c sản phẩm động vật kh&ocirc;ng r&otilde; nguồn gốc v&agrave; kh&ocirc;ng đảm bảo ti&ecirc;u chuẩn vệ sinh an to&agrave;n thực phẩm.</p>\r\n', 'vũ thế anh ', '', 'tieu-huy-lon-thoi-khia-v-o-h-noi', '["th\\u1ef1c ph\\u1ea9m b\\u1ea9n,ti\\u00eau h\\u1ee7y l\\u1ee3n th\\u1ed1i"]', 1, '', '', 'http://localhost/tuoisach/upload/images/anh%20ve%20sinh%20an%20toan%20thuc%20pham/tieu-huy.jpg', 1491258236, '0', 38),
